INSERT INTO public.feature_box_component (id, description, image_url, link, title, visible) VALUES (4, e'Dokážeš spojiť umenie s technikou? Tak naskoč do rozbehnutého vlaku moderných multimédií. Objav svet mobilných aplikácií, webov, 3D priestoru, gamedizajnu a počítačového videnia.
INSERT INTO public.feature_box_component (id, description, image_url, link, title, visible) VALUES (2, 'Nielen lekári zachraňujú životy. V našich biomedicínskych a elektrotechnických laboratóriách sa naučíš navrhovať a konštruovať lekárske prístroje a budeš pri vývoji nových medicínskych zariadení.', 'feitImages/BMI-O@3x-300x300.png', 'biomedicinske-inzinierstvo', 'Biomedicínske inžinierstvo', true);
INSERT INTO public.feature_box_component (id, description, image_url, link, title, visible) VALUES (1, 'Automatizácia je fenomén 21. storočia. Urobíme z teba špecialistu v oblasti spoľahlivej a bezpečnej komunikácie, či spracovania informácií pre inteligentnú dopravu a moderný priemysel.', 'feitImages/AT-O@3x-150x150.png', 'automatizacia', 'Automatizácia', true);
INSERT INTO public.feature_box_component (id, description, image_url, link, title, visible) VALUES (3, e'Steve Jobs, Elon Musk a … ty? Získaj vedomosti o najnovších informačno-komunikačných a sieťových technológiách a zmeň spôsob, akým budú ľudia komunikovať.
INSERT INTO public.feature_box_component (id, description, image_url, link, title, visible) VALUES (5, 'Rád spájaš výkonovku a programovanie? Nučíš sa pokročilé návrhové techniky elektrických obvodov, rozvodov a sietí. Zoznámiš sa s vysokým napätím, veľkými výkonmi a budeš mať kontrolu nad výrobou a distribúciou obrovskej energie.', 'feitImages/E-O@3x-300x300.png', 'elektrotechnika', 'Elektrotechnika', true);
INSERT INTO public.feature_box_component (id, description, image_url, link, title, visible) VALUES (6, 'Optika spôsobila novú technologickú revolúciu nielen v priemysle a prenose dát, ale aj v medicíne a bežnom ľudskom živote. Budúcnosť patrí tebe a svetlu. Tak nezahoď svoju šancu študovať hi-tech v oblasti elektrooptiky.', 'feitImages/EO-W@4x-300x300.png', 'elektrooptika', 'Elektrooptika', true);
