INSERT INTO public.posts (id, author, content, created_at, main_image_url, title) VALUES (4, 'Tester Testovic', '<p>V&nbsp;dňoch 5. až 9. mája 2025 absolvovala na Fakulte elektrotechniky a informačných technológií UNIZA, Katedre teoretickej elektrotechniky a biomedicínskeho inžinierstva Erasmus+ učiteľskú mobilitu doc. Dilek Betul Sarıdede z&nbsp;partnerskej inštitúcie Atlas University, Turecko.</p><p>Kolegyni boli počas jej návštevy odprezentované vybrané laboratóriá a predniesla prednášku pre študentov a&nbsp;zamestnancov fakulty na tému: Artificial Intelligence in Medical Imaging.</p><p></p><img src="http://localhost:8080/api/v1/images/2025/06/74a5b787-6761-4a96-a714-396b7562456c.png">', '2025-06-03 10:19:59.156277', 'http://localhost:8080/api/v1/images/2025/06/c330978b-55ca-40c0-b848-97308cf35a6e.png', 'Erasmus prednáška Artificial Intelligence in Medical Imaging');
